mapper.delete:
UPDATE T_EVENT_WATER_QUALITY T2
T2.UPDATOR = #{delUserId,jdbcType=DECIMAL},
T2.IS_VALID = '0', T2.UPDATE_TIME = SYSDATE
T2.WQ_UUID = #{wqUUID,jdbcType=VARCHAR}
T2.REPORT_UUID = #{reportUUID,jdbcType=VARCHAR}
1 = 2

mapper.select:
  		SELECT

	  	FROM T_EVENT_WATER_QUALITY T2



					T2.WQ_UUID = #{wqUUID,jdbcType=VARCHAR}


					T2.REPORT_UUID = #{reportUUID,jdbcType=VARCHAR}


					1 = 2


			AND T2.IS_VALID = '1'

  		SELECT
	  		,

	  	FROM T_EVENT_REPORT_FOCUS T1, T_EVENT_WATER_QUALITY T2
		WHERE T1.REPORT_UUID = T2.REPORT_UUID AND T1.IS_VALID IN ('1', '2') AND T2.IS_VALID = '1'


				AND T2.WQ_UUID = #{wqUUID,jdbcType=VARCHAR}


				AND T1.REPORT_ID = #{reportId,jdbcType=DECIMAL}


				AND T2.REPORT_UUID = #{reportUUID,jdbcType=VARCHAR}


				AND 1 = 2

mapper.sql:
FROM T_EVENT_REPORT_FOCUS T1, T_EVENT_WATER_QUALITY T2, ${dbuser.workflow}.WF_PROCESS_INSTANCE T3
WHERE T1.REPORT_UUID = T2.REPORT_UUID AND T1.REPORT_ID = T3.FORM_ID AND T3.FORM_TYPE_ID IN (35401, 35402, 35403)
AND T3.STATUS IN ('1','2')
AND T3.USER_ID = #{initiatorId,jdbcType=DECIMAL} 
AND T3.ORG_ID = #{initiatorOrgId,jdbcType=DECIMAL}

FROM T_EVENT_REPORT_FOCUS T1 INNER JOIN T_EVENT_WATER_QUALITY T2 ON T1.REPORT_UUID = T2.REPORT_UUID INNER JOIN ${dbuser.workflow}.WF_PROCESS_INSTANCE T3 ON T1.REPORT_ID = T3.FORM_ID AND T3.FORM_TYPE_ID IN (35401, 35402, 35403) LEFT JOIN T_ZY_RES_MARKER T10 ON T1.REPORT_ID=T10.RESOURCES_ID
AND T10.MARKER_TYPE = #{markerType,jdbcType=VARCHAR}
AND T10.MARKER_TYPE='WATER_QUALITY'
AND T10.MAP_TYPE = #{mapType,jdbcType=VARCHAR}
AND T10.MAP_TYPE = '5'
WHERE T3.STATUS IN ('1','2')

FROM T_EVENT_REPORT_FOCUS T1, ${dbuser.workflow}.WF_PROCESS_INSTANCE T3, ${dbuser.workflow}.WF_TASK T6, T_BAS_MSG T7, T_BAS_MSG_RECEIVER T8 WHERE T1.REPORT_ID = T3.FORM_ID AND T3.FORM_TYPE_ID IN (35401, 35402, 35403) AND T3.INSTANCE_ID = T6.INSTANCE_ID AND T7.BIZ_ID = T6.TASK_ID AND T7.MSG_ID = T8.MSG_ID AND T7.IS_DEL = '0'
AND T7.MODULE_CODE = #{msgModuleCode,jdbcType=VARCHAR}
AND T7.MODULE_CODE IN
#{item}
AND ( T7.SENDER_NAME LIKE '%' || #{msgKeyWord,jdbcType=VARCHAR} || '%' OR T7.MSG_CONTENT LIKE '%' || #{msgKeyWord,jdbcType=VARCHAR} || '%' )
AND T7.SEND_TIME >= #{msgSendDayStart,jdbcType=TIMESTAMP}
AND T7.SEND_TIME < #{msgSendDayEnd,jdbcType=TIMESTAMP} + 1
AND T8.RECEIVER_ID = #{msgReceiveUserId,jdbcType=DECIMAL}
AND T8.RECEIVE_ORG_ID = #{msgReceiveOrgId,jdbcType=DECIMAL}
AND T8.STATUS_ = #{msgReceiveStatus,jdbcType=VARCHAR}
AND T8.STATUS_ IN ('0', '1')

  		AND T2.IS_VALID = '1'

  			AND T2.MATTER_TYPE = #{matterType,jdbcType=VARCHAR}


  			AND T2.ADMINISTRATIVE_SACTION_TYPE LIKE '%' || #{administrativeSactionType,jdbcType=VARCHAR} || '%'


			AND T2.DATA_SOURCE IN

				#{item}
